import React, { Component } from 'react'
import { Container, Table,Row, Button, Col } from 'reactstrap'
import Axios from 'axios';
import Navigation from './Navigation';
import { Content } from 'react-mdl';


export default class Dashboard extends Component {

    constructor(props) {
        super(props)

        this.state = {
            patients:[],
            Doctors:[],

            fname: '',
            address: '',
            age:'',
            gender:'',
            phonenumber:'',
            taskDone: false,
            isEdit: false,
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    componentDidMount() {
        Axios.get('http://localhost:3000/patient', this.state.config)
            .then((response) => {
                this.setState({
                    patients: response.data
                })
            }).catch((err) => console.log(err.response))


        Axios.get('http://localhost:3000/Doctor', this.state.config)
            .then((response) => {
                this.setState({
                    Doctors: response.data,
                   
                })
            }).catch((err) => console.log(err.response))
    }

    handleTaskNameChange = (taskName) => {
        this.setState({
            taskName: taskName
        })
    }

    handleTaskDoneChange = (isDone) => {
        this.setState({
            taskDone: isDone
        })
    }

    handleTaskSubmit = (e) => {
        e.preventDefault();
        if (!this.state.taskName) return;

        Axios.post('http://localhost:3000/tasks',
            {
                name: this.state.taskName,
                done: this.state.taskDone,
                category: this.state.categoryId
            },
            this.state.config).then((response) => {
                this.setState({
                    tasks: [...this.state.tasks, response.data],
                    taskName: ''
                })
            })
    }

    handleTaskDelete = (patient) => {
        Axios.delete(`http://localhost:3000/patient/${patient}`, this.state.config)
            .then((response) => {
                const filteredpatient = this.state.patients.filter((patient) => {
                    return patient._id !== patient
                })
                this.setState({
                    patient: filteredpatient
                })
                alert('Delete Suucessfully')
                window.location.reload(false);
            })
    }


    handleTaskDelete = (D) => {
        Axios.delete(`http://localhost:3000/patient/${patient}`, this.state.config)
            .then((response) => {
                const filteredpatient = this.state.patients.filter((patient) => {
                    return patient._id !== patient
                })
                this.setState({
                    patient: filteredpatient
                })
                alert('Delete Suucessfully')
                window.location.reload(false);
            })
    }



    handleTaskUpdate = (e) => {
        e.preventDefault();
        Axios.put(`http://localhost:3000/tasks/${this.state.taskId}`,
            {
                name: this.state.taskName,
                done: this.state.taskDone,
                category: this.state.categoryId
            },
            this.state.config)
            .then((response) => {
                const updatedTasks = this.state.tasks.map((task) => {
                    if (task._id === response.data._id) {
                        task = response.data
                    }
                    return task;
                })
                this.setState({
                    tasks: updatedTasks,
                    taskName: '',
                    taskDone: false,
                    taskId: '',
                    isEdit: false
                })
            }).catch((err) => console.log(err.response));
    }


    itemClick = (task) => {
        this.setState({
            isEdit: !this.state.isEdit,
            taskId: task._id,
            taskName: task.name,
            taskDone: task.done
        })
        if (task.category) {
            this.setState({
                categoryId: task.category._id
            })
        } else this.setState({
            categoryId: this.state.categories[0]._id
        })

        if (this.state.isEdit) {
            this.setState({
                taskId: '',
                taskName: '',
                taskDone: false,

            })
        }
    }

    handleCategoryChange = (e) => {
        this.setState({
            categoryId: e.target.value
        })
    }

    render() {
        
        return (
            <React.Fragment>
                <Navigation />
                <Content>
                    <Row>
                        <Col md="6">

                        <Table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Address</th>
          <th>Age</th>
          <th>Phone Number</th>
          <th>Disease</th> 
          <th>Prescription</th>
          <th>Action </th>
        </tr>
      </thead>

      <tbody>
          {
        this.state.patients.map((patient)=>{
           return <tr>
          <td>{patient.fname}</td>
                        <td>{patient.address}</td>
                        <td>{patient.age}</td>
                        <td>{patient.phonenumber}</td> 
                        <td>{patient.disease}</td>
                        <td>{patient.prescription}</td>
                        <td> <Button >Edit</Button>  &nbsp;
                        <Button class="btn btn-primary" onClick={()=> this.delete(patient._id)}>Delete</Button></td>
        </tr>
        
        })
    }
      </tbody>

    </Table>
&nbsp;

                        </Col>


                        <Col md="6" >

<Table>
<thead >
<tr> 
<th>Name</th>
<th>Address</th>
<th>Age</th>
<th> Number</th>
<th>Specialist</th> 
<th>Action </th>
</tr>
</thead>

<tbody>
{
this.state.Doctors.map((doctor)=>{
return <tr>
<td>{doctor.fname}</td>
<td>{doctor.address}</td>
<td>{doctor.age}</td>
<td>{doctor.phonenumber}</td> 
<td>{doctor.specialist}</td>

<td> <Button >Edit</Button>  &nbsp;
<Button class="btn btn-primary" onClick={()=> this.delete(doctor._id)}>Delete</Button></td>
</tr>

})
}
</tbody>

</Table>


</Col>

                    </Row>
                
                </Content>
            </React.Fragment>
        )
    }
}
